import React from 'react';
import classNames from 'classnames';
import { SectionTilesProps } from '../../utils/SectionProps';
import SectionHeader from './partials/SectionHeader';

const prodURL = 'https://langkit-prod.herokuapp.com/parse-cmd'

const propTypes = {
  ...SectionTilesProps.types
}

const defaultProps = {
  ...SectionTilesProps.defaults
}

const GenerationSection = ({
  className,
  topOuterDivider,
  bottomOuterDivider,
  topDivider,
  bottomDivider,
  hasBgColor,
  invertColor,
  pushLeft,
  ...props
}) => {

  const outerClasses = classNames(
    'features-tiles section',
    topOuterDivider && 'has-top-divider',
    bottomOuterDivider && 'has-bottom-divider',
    hasBgColor && 'has-bg-color',
    invertColor && 'invert-color',
    className
  );

  const innerClasses = classNames(
    'features-tiles-inner section-inner pt-0',
    topDivider && 'has-top-divider',
    bottomDivider && 'has-bottom-divider'
  );

  const tilesClasses = classNames(
    'tiles-wrap center-content',
    pushLeft && 'push-left'
  );

  const sectionHeader = {
    title: ' ',
    paragraph: ''
  };

  return (
    <section
      {...props}
      className={outerClasses}
    >
      <div className="container">
        <div className={innerClasses}>
          <SectionHeader data={sectionHeader} className="center-content" />
          <div className={tilesClasses}>
            <div className="form">
              <input type="text" id="prompt" className="form__input" autoCorrect="off" placeholder=" "/>
              <label for="prompt" className="form__label">Generate Vocab</label>
            </div>
            <div className="grid-ctr">
              <div className="basic-grid" id="generated-vocab">
                <div className='card'>
                  <div className='src'>Word</div>
                  <div className='trn'>Palabra</div>
                </div>
                <div className='card'>NO</div>
                <div className='card'>NO</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

GenerationSection.propTypes = propTypes;
GenerationSection.defaultProps = defaultProps;



document.addEventListener('keypress', async function (event) {
  var name = event.key;
  var code = event.code;

  if (name === 'Enter') {
    console.log("submitted");
    const newEl = document.createElement("div");
    newEl.setAttribute("class", "card");

    const data = {
      'cmd': document.getElementById("prompt").value.toString()
    };

    let rawResponse = await fetch(prodURL, {
      method: 'POST',
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json"
      },
      body: JSON.stringify(data)
    });

    newEl.innerHTML = document.getElementById("prompt").value;
    document.getElementById("generated-vocab").appendChild(newEl);
    document.getElementById("prompt").value = '';
  }
})

export default GenerationSection;
