import React from 'react';
import classNames from 'classnames';
import { SectionTilesProps } from '../../utils/SectionProps';
import SectionHeader from './partials/SectionHeader';

const prodURL = 'https://langkit-prod.herokuapp.com/parse-cmd'

const propTypes = {
  ...SectionTilesProps.types
}

const defaultProps = {
  ...SectionTilesProps.defaults
}

const GenerationSection = ({
  className,
  topOuterDivider,
  bottomOuterDivider,
  topDivider,
  bottomDivider,
  hasBgColor,
  invertColor,
  pushLeft,
  ...props
}) => {

  const outerClasses = classNames(
    'features-tiles section',
    topOuterDivider && 'has-top-divider',
    bottomOuterDivider && 'has-bottom-divider',
    hasBgColor && 'has-bg-color',
    invertColor && 'invert-color',
    className
  );

  const innerClasses = classNames(
    'features-tiles-inner section-inner pt-0',
    topDivider && 'has-top-divider',
    bottomDivider && 'has-bottom-divider'
  );

  const tilesClasses = classNames(
    'tiles-wrap center-content',
    pushLeft && 'push-left'
  );

  const sectionHeader = {
    title: ' ',
    paragraph: ''
  };

  return (
    <section
      {...props}
      className={outerClasses}
    >
      <div className="container">
        <div className={innerClasses}>
          <SectionHeader data={sectionHeader} className="center-content" />
          <div className={tilesClasses}>
            <div className="form">
              <input type="text" id="prompt" className="form__input" autoCorrect="off" placeholder=" "/>
              <label for="prompt" className="form__label">Generate Vocab</label>
            </div>
            <div className="grid-ctr">
              <div className="basic-grid" id="generated-vocab">
                <div>
                  <div id='src'>I'm feeling ...</div>
                  <div id='mid'></div>
                  <div id='trn'>Me siento ...</div>
                </div>
                <div>
                  <div id='src'>energized</div>
                  <div id='mid'></div>
                  <div id='trn'>lleno de energía</div>
                </div>
                <div>
                  <div id='src'>alert</div>
                  <div id='mid'></div>
                  <div id='trn'>alerta</div>
                </div>
                <div>
                  <div id='src'>focused</div>
                  <div id='mid'></div>
                  <div id='trn'>concentrado</div>
                </div>
                <div>
                  <div id='src'>after drinking coffee</div>
                  <div id='mid'></div>
                  <div id='trn'>despues de beber café</div>
                </div>
                <div>
                  <div id='src'>I am in love with coffee</div>
                  <div id='mid'></div>
                  <div id='trn'>Estoy enamorada del café</div>
                </div>
                <div>
                  <div id='src'>Coffee is my best friend</div>
                  <div id='mid'></div>
                  <div id='trn'>El café es mi mejor amigo</div>
                </div>
                <div>
                  <div id='src'>Coffee</div>
                  <div id='mid'></div>
                  <div id='trn'>Café</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

GenerationSection.propTypes = propTypes;
GenerationSection.defaultProps = defaultProps;

function add_to_global_window (pair) {
  let list = document.getElementById("global-vocab");

  let newDiv = document.createElement("div");
  let rDiv = document.createElement("div");
  let lDiv = document.createElement("div");
  rDiv.setAttribute("id", 'trn');
  lDiv.setAttribute("id", 'src');
  rDiv.innerHTML = pair[0];
  lDiv.innerHTML = pair[1];

  newDiv.appendChild(lDiv);
  newDiv.appendChild(rDiv);

  list.appendChild(newDiv);
}

function add_to_generation_window (pair) {
  let list = document.getElementById("generated-vocab");

  let newDiv = document.createElement("div");
  let rDiv = document.createElement("div");
  let lDiv = document.createElement("div");
  rDiv.setAttribute("id", 'trn');
  lDiv.setAttribute("id", 'src');
  rDiv.innerHTML = pair[0];
  lDiv.innerHTML = pair[1];

  newDiv.appendChild(lDiv);
  newDiv.appendChild(rDiv);

  newDiv.addEventListener('click', function () {
    add_to_global_window(pair);
  });

  list.appendChild(newDiv);
}

function add_demo_stuff() {
  add_to_generation_window(["I'm feeling ...", "Me siento ..."]);
  add_to_generation_window(["I'm feeling ...", "Me siento ..."]);
  add_to_generation_window(["I'm feeling ...", "Me siento ..."]);
  add_to_generation_window(["I'm feeling ...", "Me siento ..."]);
  add_to_generation_window(["I'm feeling ...", "Me siento ..."]);
  add_to_generation_window(["I'm feeling ...", "Me siento ..."]);
  add_to_generation_window(["I'm feeling ...", "Me siento ..."]);
}


